group TransformGroupObservation(source src: questionnaireResponse, source bundle:Bundle, target groupObservation: Observation, target entry)
{
  src -> groupObservation.id = uuid() then
    SetObservationFullUrl(groupObservation, entry) "grp-obsnFullUrl"; 
  src -> groupObservation.status = 'final' "grp-obsn-status"; 
  src -> groupObservation.category = cc('http://terminology.hl7.org/CodeSystem/observation-category', 'social-history', 'Social History') "grp-obsn-cat-social-history";
  src -> groupObservation.category = cc('http://terminology.hl7.org/CodeSystem/observation-category', 'survey', 'Survey') "grp-obsn-cat-survey";

  src -> groupObservation.meta = create('Meta') as newMeta then {
    src -> newMeta.profile = 'http://hl7.org/fhir/us/sdoh-clinicalcare/StructureDefinition/SDOHCC-ObservationScreeningResponse' "grp-obsn-meta-profile";
  } "grpObsMeta"; 

  src.authored as authored -> groupObservation.effective = authored;
  src.subject as qSubject -> groupObservation.subject = create('Reference') as newReference then {
    qSubject.reference as qReference -> newReference.reference = qReference "grp-obsn-subj-ref";
    qSubject.display as qDisplay -> newReference.display = qDisplay "grp-obsn-subj-display";
  } "grp-obsn-subject";  

  src -> groupObservation.code = cc('http://loinc.org', '93025-5') "grp-obsn-code";   

  //only add observations that are derived from the answers
  bundle.entry as entries -> groupObservation.hasMember = create('Reference') as df then {
    entries.resource as obs where status.exists() and hasMember.exists().not() -> df.reference = reference(obs) "grp-obsn-has-member-reference"; 
  } "grp-obsn-has-member";

  src.id as id -> groupObservation.derivedFrom = create('Reference') as newReference then {
    id -> newReference.reference = append('QuestionnaireResponse/', id) "grp-obsn-derived-from-reference";
  } "grp-obsn-derived-from";

  src -> entry.request as request then {
    src -> request.method = 'POST' "obsn-request-method";
    src -> request.url = 'Observation' "obsn-request-url";
  } "obsn-entry-request";    
}
